mapper.select:
SELECT m_id, s_id, category, name, price, m_info,
       m_picture_url, liked, reg_date, modi_date
FROM menu WHERE m_id = #{mId}

mapper.update:
UPDATE menu_option
SET m_option = #{mOption}, content = #{content}, price = #{price},
    modi_date = SYSDATE(), status = #{status}
WHERE mo_id = #{moId}

UPDATE shop
SET status = #{status}
WHERE s_id = #{sId}
